using System.Text.Json;using System.Text.Json.Serialization;namespace CurrencyConverter.Fluent.Plugin{    internal class CurrencyApiResponse    {        [JsonPropertyName("date")]        public string? Date { get; set; }        [JsonExtensionData]        public Dictionary<string, JsonElement>? AdditionalData { get; set; }        public Dictionary<string, double>? GetExchangeRates(string baseCurrency)        {            if (AdditionalData == null || !AdditionalData.TryGetValue(baseCurrency.ToLowerInvariant(), out JsonElement ratesElement))                return null;            try            {                return JsonSerializer.Deserialize<Dictionary<string, double>>(ratesElement.GetRawText(),                    new JsonSerializerOptions { PropertyNameCaseInsensitive = true });            }            catch            {                return null;            }        }    }}