using Blast.Core.Results;namespace CurrencyConverter.Fluent.Plugin{    public enum CurrencyOperationType    {        Copy,        OpenWebsite,        ConvertToOther    }    public class CurrencyConversionSearchOperation : SearchOperationBase    {        public new CurrencyOperationType OperationType { get; }        public string TargetCurrency { get; }        public double ConvertedAmount { get; }        public static CurrencyConversionSearchOperation CopyResultOperation { get; } =            new CurrencyConversionSearchOperation("Copy", "Copy conversion result", "\uE8C8", CurrencyOperationType.Copy);        public static CurrencyConversionSearchOperation OpenExchangeRateOperation { get; } =            new CurrencyConversionSearchOperation("View Exchange Rates", "Open currency exchange rates website", "\uE774", CurrencyOperationType.OpenWebsite);        public CurrencyConversionSearchOperation(string name, string description, string iconGlyph,             CurrencyOperationType operationType, string targetCurrency = null, double convertedAmount = 0)             : base(name, description, iconGlyph)        {            OperationType = operationType;            TargetCurrency = targetCurrency;            ConvertedAmount = convertedAmount;        }        public static CurrencyConversionSearchOperation CreateConversionOperation(string targetCurrency, string targetCurrencyName, double amount, double convertedAmount)        {            return new CurrencyConversionSearchOperation(                $"{convertedAmount} {targetCurrencyName}",                $"Convert to {targetCurrency}: {amount} â†’ {convertedAmount} {targetCurrencyName}",                "\uE8AB",                CurrencyOperationType.ConvertToOther,                targetCurrency,                convertedAmount);        }    }}